import React, { useEffect, useState } from 'react';
import axios from 'axios';

function AgentLists() {
  const [agents, setAgents] = useState([]);
  const [selected, setSelected] = useState('');
  const [items, setItems] = useState([]);
  const [loading, setLoading] = useState(false);
  const [editId, setEditId] = useState(null);
  const [editData, setEditData] = useState({ name: '', email: '', mobile: '' });
  const [csvUploaded, setCsvUploaded] = useState(false);
  const token = localStorage.getItem('token');

  useEffect(() => {
    axios.get('http://localhost:5000/api/agents', { headers: { Authorization: `Bearer ${token}` } })
      .then(res => setAgents(res.data));
    // Check if CSV was uploaded in this session
    setCsvUploaded(sessionStorage.getItem('csvUploaded') === 'true');
    if (!sessionStorage.getItem('csvUploaded')) {
      setItems([]);
    }
  }, [token]);



  const handleEdit = (id) => {
    const agent = agents.find(a => a._id === id);
    setEditId(id);
    setEditData({ name: agent.name, email: agent.email, mobile: agent.mobile });
  };

  const handleEditChange = (e) => {
    const { name, value } = e.target;
    setEditData(prev => ({ ...prev, [name]: value }));
  };

  const handleEditCancel = () => {
    setEditId(null);
    setEditData({ name: '', email: '', mobile: '' });
  };

  const handleEditSubmit = async (id) => {
    setLoading(true);
    try {
      const res = await axios.put(`http://localhost:5000/api/agents/${id}`, editData, { headers: { Authorization: `Bearer ${token}` } });
      setAgents(agents.map(a => a._id === id ? res.data : a));
      setEditId(null);
      setEditData({ name: '', email: '', mobile: '' });
    } catch (err) {
      alert('Failed to update agent');
    }
    setLoading(false);
  };

  const handleDelete = async (id) => {
    if (!window.confirm('Are you sure you want to delete this agent?')) return;
    setLoading(true);
    try {
      await axios.delete(`http://localhost:5000/api/agents/${id}`, { headers: { Authorization: `Bearer ${token}` } });
      setAgents(agents.filter(a => a._id !== id));
      if (selected === id) {
        setSelected('');
        setItems([]);
      }
    } catch (err) {
      alert('Failed to delete agent');
    }
    setLoading(false);
  };

  const handleSelect = async e => {
    const agentId = e.target.value;
    setSelected(agentId);
    if (agentId && csvUploaded) {
      const res = await axios.get(`http://localhost:5000/api/lists/agent/${agentId}`, { headers: { Authorization: `Bearer ${token}` } });
      setItems(res.data);
    } else {
      setItems([]);
    }
  };

  return (
    <div className="app-container">
      <h2>Agent Lists</h2>
      <select onChange={handleSelect} value={selected} disabled={loading} className="form-select mb-3">
        <option value="">Select Agent</option>
        {agents.map(a => <option key={a._id} value={a._id}>{a.name}</option>)}
      </select>
      <ul className="list-group mb-3">
        {agents.map(a => (
          <li key={a._id} className="list-group-item d-flex align-items-center justify-content-between">
            {editId === a._id ? (
              <form onSubmit={e => { e.preventDefault(); handleEditSubmit(a._id); }} className="d-flex align-items-center w-100 gap-2">
                <input name="name" value={editData.name} onChange={handleEditChange} required className="form-control" style={{width:'100px'}} />
                <input name="email" value={editData.email} onChange={handleEditChange} required className="form-control" style={{width:'160px'}} />
                <input name="mobile" value={editData.mobile} onChange={handleEditChange} required className="form-control" style={{width:'120px'}} />
                <button type="submit" className="btn btn-success btn-sm">Save</button>
                <button type="button" onClick={handleEditCancel} className="btn btn-secondary btn-sm">Cancel</button>
              </form>
            ) : (
              <>
                <span>
                  <strong>{a.name}</strong> <span className="text-primary">- {a.email}</span> <span className="text-success">- {a.mobile}</span>
                </span>
                <div className="d-flex gap-2">
                  <button onClick={() => handleEdit(a._id)} className="btn btn-info btn-sm">Edit</button>
                  <button onClick={() => handleDelete(a._id)} className="btn btn-danger btn-sm">Delete</button>
                </div>
              </>
            )}
          </li>
        ))}
      </ul>
      {selected && (
        <div className="mb-3 p-3 bg-light rounded shadow-sm">
          <strong>Name:</strong> {agents.find(a => a._id === selected)?.name}<br/>
          <strong>Email:</strong> {agents.find(a => a._id === selected)?.email}<br/>
          <strong>Mobile:</strong> {agents.find(a => a._id === selected)?.mobile}
        </div>
      )}
      <ul className="list-group">
        {items.map((item, idx) => (
          <li key={idx} className="list-group-item">
            <strong>{item.firstName}</strong> <span className="text-primary">- {item.phone}</span> <span className="text-success">- {item.notes}</span>
          </li>
        ))}
      </ul>
    </div>
  );
}
export default AgentLists;
